class ItemBaseListData {
  String dataType;
  List<ItemList> itemList;
  int count;
  String text;
  String icon;
  Header header;
  Content content;
  String title;
  Cover cover;
  String description;
  String category;

  int duration ;
  int id ;
  ItemBaseListData(
      {this.dataType,
      this.itemList,
      this.count,
      this.text,
      this.header,
      this.icon,
      this.content,
      this.title,
      this.description

      ,
        this.id,
      this.category  ,
        this.duration
      });

  ItemBaseListData.fromJson(dynamic json) {
    dataType = json["dataType"];
    if (json["itemList"] != null) {
      itemList = [];
      json["itemList"].forEach((v) {
        itemList.add(ItemList.fromJson(v));
      });
    }
    count = json["count"];
    id = json["id"];
    icon = json["icon"];
    text = json["text"];
    header = json['header'] != null ? Header.fromJson(json['header']) : null;
    content =
        json['content'] != null ? Content.fromJson(json['content']) : null;
    title = json["title"];

    cover =
    json['cover'] != null ? Cover.fromJson(json['cover']) : null;
    description = json["description"];
    category = json["category"];
    duration = json["duration"];


  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["dataType"] = dataType;
    if (itemList != null) {
      map["itemList"] = itemList.map((v) => v.toJson()).toList();
    }
    map["count"] = count;
    map["icon"] = icon;
    if (this.header != null) {
      map['data'] = this.header.toJson();
    }
    if (this.content != null) {
      map['content'] = this.content.toJson();
    }

    if (this.cover != null) {
      map['cover'] = this.cover.toJson();
    }
    map["count"] = title;
    map["icon"] = description;
    map["category"] = category;
    map["duration"] = duration ;
    map["id"] = id ;


    return map;
  }
}
